(()=>{var e=document.getElementsByClassName("video"),t=document.getElementsByClassName("controls");for(const s of e){const n=s.getElementsByTagName("video")[0];for(const e of t){e.setAttribute("data-state","visible"),e.addEventListener("mouseover",t=>{n.paused||e.setAttribute("data-state","visible")},!1),e.addEventListener("mouseout",t=>{n.paused||e.setAttribute("data-state","hidden")},!1);const s=e.getElementsByClassName("playpause")[0];s.addEventListener("click",e=>{n.paused?(n.play(),s.innerHTML="\u23F8"):(n.pause(),s.innerHTML="\u23F5")},!1)}}})()